generated.css:
html{
    font-size: 16px;
}

body {
    background-color: #F8F9FA;
}

#chance {
    padding-left: 30px;
    font-weight: 200;
}

.chance-text {
    border: 1px solid #000;
    height: 80px;
    line-height: 80px;
    border-top-right-radius: 10px;
    border-top-left-radius: 10px;
    background-color: #9b3be5;
    color: #F8F9FA;
    text-align-last: left;
    padding-left: 45px;
}

#correct-score {
    color: green;
    font-weight: 500;
    padding-left: 10px;;
}

#wrong-score {
    color: red;
    font-weight: 500;
    padding-left: 10px;;
}

.correct-text {
    text-align-last: left;
    padding-left: 40px;
    font-weight: 200;
}

.fas {
    font-size: 1.4rem;
}

.guess{
    margin-left: 10px;
    margin-right: 10px;
    margin-bottom: 10px;
}

#guesses, #guesses2{
    margin: auto;
    margin-bottom: 80px;
    max-width: 750px;
    border: 1px solid #F8F9FA;
}

.play-gain {
    background-color: #c397e52e;
    border-radius: 20px;
    margin-top: 140px;
    margin-bottom: 100px;
}

.gain-slider {
   width: 100px;
   position: relative;
   left: 40px;
   top: 0px;
   transform: rotate(270deg);
}

.wrong-text {
    text-align-last: left;
    padding-left: 40px;
    font-weight: 200;
}

.visible-easy{
    display: none;
}

.visible-hard{
    display: none;
}

.easy {
    display: block;
} 

.hard {
    display: block;
}  

.nav-choices > *{
    margin: 5px;
}

.navbar {
    padding: 0 80px;
    background-image: linear-gradient(to top right, #7200ca, #940aff);
    color: #F8F9FA;
}

.navbar-brand {
    font-size: 2rem;
    color: #F8F9FA;
    margin-bottom: 10px;
    letter-spacing: 3px;
}

.fixed-bottom {
    padding-top: 13px;
    height: 60px;
}

.correct-wrong{
    padding: 20px;
    padding-top: 50px;
    margin-left: -80px;
    display: flex;
    justify-content: space-around;
}

.play {
    width: 173px;
    height: 100px;
    box-shadow: 4px 4px 7px rgba(0, 0, 0, 0.14);
    border-radius: 10px;
    font-size: 2rem;
}

.play:focus {
    outline: none;
}

.play:hover {
    cursor: pointer;
}

.score-box{
    border: 1px solid #000;
    border-radius: 10px;
    box-shadow: 2px 3px 1px 2px lightgrey;
    max-width: 500px;
    margin: auto;
    color: #333A40;
}

select{
    height: 38px;
    background-color: transparent;
    color: #F8F9FA;
    margin-right: 20px;
}

.waveform {
    color: #333A40;
}

.waveform-select {
    margin-right: 20px;
}


@media (max-width: 820px) {
    .correct-wrong{
        padding: 20px;
        padding-top: 70px;
    }

    .navbar {
        padding-top: 15px;
        padding-bottom: 24px;
    }

    .navbar-brand {
        margin: auto;
    }

    .fixed-bottom {
        padding-top: 33px;
    }

    .nav-choices {
        margin: auto;
    }

    .play-gain {
        margin-top: 120px;
    }
}

@media (max-width: 460px) {

    #guesses, #guesses2{
        max-width: 450px;
        margin-bottom: -52px;
    }
    .navbar-brand {
        font-size: 2rem;
        margin-left: -50px;
    }

    .nav-choices {
        margin-left: -20px;
    }

    .fixed-bottom {
     padding-top: 40px;
     height: 60px;
    }

    .play-gain {
        margin-top: 80px;
    }

    .score-box{
        border: 1px solid #000;
        border-radius: 10px;
        box-shadow: 2px 3px 1px 2px lightgrey;
        max-width: 360px;
        margin: auto;
        color: #333A40;
        margin-top: 120px;
    }
}
